(function(){var o=document.querySelectorAll(".categories-dropdowns"),e=document.querySelectorAll(".main-dropdown");o.forEach(function(n){n.addEventListener("click",function(){e.forEach(function(t){t.classList.add("active")})})});var c=document.querySelectorAll(".main-dropdown .categories-dropdowns__item");c.forEach(function(n){n.onclick=()=>{c.forEach(t=>{t.children[0].innerHTML!=n.children[0].innerHTML&&t.classList.remove("is-expanded")}),n.classList.toggle("is-expanded")}})})();function d(o){var e=document.getElementById("dropdown-toggle");e.style.display=="block"?e.style.display="none":e.style.display="block"}document.querySelector(".toggleDropdown").onclick=function(){d()};document.querySelector(".main-content").onclick=()=>{var o=document.getElementById("dropdown-toggle");o.style.display=="block"&&(o.style.display="none")};
